// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Transfer.sol"; // Import the Transfer contract



contract Proxy {
    mapping(bytes4 => address) private implementations;
    Transfer private transferContract; 

    constructor(address _transferAddress) {
        transferContract = Transfer(_transferAddress); 
    }

    // Fallback function
    fallback() external {
        address implementation = implementations[msg.sig];
        require(implementation != address(0), "Function implementation not found");
        (bool success, ) = implementation.delegatecall(msg.data);
        require(success, "Function call failed");
    }

    // Function to update implementation address
    function setImplementation(bytes4 functionId, address implementation) external {
        implementations[functionId] = implementation;
    }

    // Function to call the _transfer function of the Transfer contract
    function callTransfer(address payable _to) external payable {
        // Delegate the call to the _transfer function of the Transfer contract
        transferContract._transfer{value: msg.value}(_to);
    }

    // Function to generate function ID for _transfer function
    function getTransferFunctionId() external pure returns (bytes4) {
        return bytes4(keccak256("_transfer(address)"));
    }
}

