//SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.0;

contract Transfer {
    event transaction(
        address indexed from,
        address to,
        uint amount

    );

    function _transfer(
        address payable _to
    ) public payable {
        _to.transfer(msg.value);
        emit transaction(msg.sender, _to, msg.value);
    }

    function saveTx(
        address from,
        address to,
        uint amount

    ) public {
        emit transaction(from, to, amount);
    }


}

